import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { SearchService } from 'src/app/youtube/services/search.service';
import { SettingsService } from 'src/app/youtube/services/settings.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent implements OnInit {
  placeholder = 'What are you want to find out?';

  hRouter!: Router;

  constructor(
    private settingsService: SettingsService,
    private router: Router,
    private searchService: SearchService,
  ) {}

  ngOnInit(): void {
    this.hRouter = this.router;
  }

  search(value: string) {
    this.searchService.onSearch(value);
  }

  onClickSettings() {
    this.settingsService.toggleSettings();
  }
}
<header>
  <div class="header_container">
    <div class="header_search">
      <app-logo></app-logo>
      <div class="search-field">
        <input type="text" id="search-input" [placeholder]="placeholder" #searchInput (input)="search(searchInput.value)"/>
      </div>
      <app-button
        [type]="'button'"
        [disabled]="false"
        (click)="onClickSettings()"
        ><img
          src="assets/img/settings-button-icon.svg"
          alt="settings icon"
          class="icon"
      /></app-button>
    </div>
    <app-login-information></app-login-information>
    <div class="to-admin">
      <app-button
        [type]="'button'"
        [disabled]="false"
        (click)="hRouter.navigateByUrl('/admin')"
        >To admin page</app-button
      >
    </div>
  </div>
</header>
