import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { StrongPasswordValidator } from '../../validators/strong-password.validator';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent implements OnInit {
  isLoggedIn = this.authService.getIsLoggedIn();

  loginForm!: FormGroup<{
    login: FormControl<string | null>;
    password: FormControl<string | null>;
  }>;

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit(): void {
    this.loginForm = new FormGroup({
      login: new FormControl<string | null>('', [
        Validators.required, Validators.email,
      ]),
      password: new FormControl<string | null>('', [
        Validators.required, StrongPasswordValidator(),
      ]),
    });
  }

  get login() {
    return this.loginForm.get('login');
  }

  get password() {
    return this.loginForm.get('password');
  }

  onLogin(event: Event) {
    event?.preventDefault();
    const { login, password } = this.loginForm.value;
    this.isLoggedIn = this.authService.login({ login, password });
    if (this.isLoggedIn) {
      this.router.navigateByUrl('/youtube');
    }
  }

  onLogout(event: Event) {
    event?.preventDefault();
    this.isLoggedIn = this.authService.logout();
  }
}
<div class="login_container">
  <p class="login_subheader">Login</p>
  <form [formGroup]="loginForm" class="login_form form">
    <div class="form-control_wrapper">
      <label for="auth-login">login</label>
      <input
        type="email"
        name="auth-login"
        id="auth-login"
        required
        formControlName="login"
      />
      <div
        *ngIf="login?.invalid && (login?.dirty || login?.touched)"
        class="error-message"
      >
        <div *ngIf="login?.errors?.['required']">
          Please enter a login email
        </div>
        <div *ngIf="login?.errors?.['email']">The login email is invalid.</div>
      </div>
    </div>
    <div class="form-control_wrapper">
      <label for="auth-password">password</label>
      <input
        type="password"
        name="auth-password"
        id="auth-password"
        required
        formControlName="password"
      />
      <div
        *ngIf="password?.invalid && (password?.dirty || password?.touched)"
        class="error-message"
      >
        <div *ngIf="password?.errors?.['required']">
          Please enter a password
        </div>
        <div *ngIf="password?.errors?.['strongPassword']">
          Your password isn't strong enough: {{password?.errors?.['strongPassword'].message}}
        </div>
      </div>
    </div>
    <app-button [type]="'submit'" [disabled]="!loginForm.valid" (click)="onLogin($event)" *ngIf="!isLoggedIn"
      >Login</app-button
    >
    <app-button [type]="'submit'" [disabled]="false" (click)="onLogout($event)" *ngIf="isLoggedIn"
      >Logout</app-button
    >
  </form>
</div>
