import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { YoutubeService } from '../../services/youtube.service';
import { IItem } from '../../models/search-item.model';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent implements OnInit {
  cardRouter!: Router;

  item!: IItem;

  date!: Date;

  constructor(
    private youtubeService: YoutubeService,
    private route: ActivatedRoute,
    private router: Router,
  ) {}

  ngOnInit(): void {
    const itemId = this.route.snapshot.params['id'];
    if (itemId) {
      this.youtubeService.getItem(itemId).subscribe(
        // eslint-disable-next-line no-return-assign, prefer-destructuring
        (data) => (this.item = data.items.filter((i) => i.id.toString() === itemId)[0]),
      );
    }
    // eslint-disable-next-line no-return-assign
    this.cardRouter = this.router;
    this.date = new Date(this.item?.snippet.publishedAt);
  }
}
<div class="card_container" *ngIf="item">
  <app-button
    [type]="'button'"
    [disabled]="false"
    (click)="cardRouter.navigateByUrl('/youtube')"
    [appSetColor]="date"
  >
    <img src="assets/img/back.png" alt="arrow back" />
  </app-button>
  <div class="card_content">
    <div class="card_video">
      <iframe [src]="'https://www.youtube.com/embed/'+item.id"
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
        allowfullscreen
      ></iframe>
      <img [src]="item.snippet.thumbnails.default.url" alt="thumbnails" />
    </div>
    <div class="card_details">
      <div class="card_top">
        <h1 class="card_title">{{ item.snippet.title }}</h1>
        <p class="card_date">
          {{ item.snippet.publishedAt | date : "fullDate" }}
        </p>
      </div>
      <div class="card_description">
        <p class="card_description_subtitle">Description:</p>
        <p class="card_description_text">
          {{ item.snippet.description }}
        </p>
      </div>
      <div class="card_statistics">
        <div class="card_info">
          <img src="assets/img/viewed.svg" alt="viewed" class="icon" />
          <span class="count">{{ item.statistics.viewCount }}</span>
        </div>
        <div class="card_info">
          <img src="assets/img/liked.svg" alt="liked" class="icon" />
          <span class="count">{{ item.statistics.likeCount }}</span>
        </div>
        <div class="card_info">
          <img src="assets/img/dislike.svg" alt="disliked" class="icon" />
          <span class="count">{{ item.statistics.dislikeCount }}</span>
        </div>
        <div class="card_info">
          <img src="assets/img/commented.svg" alt="commented" class="icon" />
          <span class="count">{{ item.statistics.commentCount }}</span>
        </div>
      </div>
      <div class="card_bottom" [appSetColor]="date"></div>
    </div>
  </div>
</div>
