import { Component, DoCheck, OnInit } from '@angular/core';
import { YoutubeService } from '../../services/youtube.service';
import { SettingsService } from '../../services/settings.service';

@Component({
  selector: 'app-search-results',
  templateUrl: './search-results.component.html',
  styleUrls: ['./search-results.component.scss'],
})
export class SearchResultsComponent implements DoCheck, OnInit {
  results = [];

  openSorting = this.settingsService.getSettings();

  sortByDate = this.settingsService.getSortByDate();

  sortByView = this.settingsService.getSortByView();

  filterString = this.settingsService.getFilterString();

  constructor(
    private youtubeservice: YoutubeService,
    private settingsService: SettingsService
  ) {}

  ngOnInit() {
    this.youtubeservice.setItems().subscribe((resp) => {
      console.log(resp); // eslint-disable-line
    });
  }

  ngDoCheck() {
    // this.results = this.youtubeservice.getItems();
    this.openSorting = this.settingsService.getSettings();
    this.sortByDate = this.settingsService.getSortByDate();
    this.sortByView = this.settingsService.getSortByView();
    this.filterString = this.settingsService.getFilterString();
  }
}
<app-sorting *ngIf="openSorting && results.length"></app-sorting>
<div class="search_results" *ngIf="results.length">
  <app-search-item *ngFor="let item of results | filter: filterString | orderBy: sortByDate | sortBy: sortByView" [item]="item"></app-search-item>
</div>
