import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import {
  Observable, Subject, debounceTime, distinctUntilChanged, switchMap,
} from 'rxjs';
import { AuthService } from 'src/app/auth/services/auth.service';
import { IItem } from 'src/app/youtube/models/search-item.model';
import { SettingsService } from 'src/app/youtube/services/settings.service';
import { YoutubeService } from 'src/app/youtube/services/youtube.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent implements OnInit {
  placeholder = 'What are you want to find out?';

  hRouter!: Router;

  items!: Observable<IItem[]>;

  private searchValue = new Subject<string>();

  constructor(
    private settingsService: SettingsService,
    private youtubeService: YoutubeService,
    private authService: AuthService,
    private router: Router,
  ) {}

  ngOnInit(): void {
    this.hRouter = this.router;
    this.items = this.searchValue.pipe(
      debounceTime(500),
      distinctUntilChanged(),
      switchMap((value: string) => this.youtubeService.setItems(value)),
    );
  }

  search(value: string): void {
    this.searchValue.next(value);
  }

  onClickSearch() {
    if (this.authService.getIsLoggedIn()) {
      if (this.settingsService.openSettings) {
        this.settingsService.toggleSettings();
      }
      this.settingsService.clearSettings();
      this.youtubeService.setItems('');
      this.router.navigateByUrl('/youtube');
    }
  }

  onClickSettings() {
    this.settingsService.toggleSettings();
  }
}
<header>
  <div class="header_container">
    <div class="header_search">
      <app-logo></app-logo>
      <div class="search-field">
        <input type="text" id="search-input" [placeholder]="placeholder" #searchInput (input)="search(searchInput.value)"/>
        <app-button
          [type]="'button'"
          [disabled]="false"
          (click)="onClickSearch()"
        >
          <span>search</span>
        </app-button>
      </div>
      <app-button
        [type]="'button'"
        [disabled]="false"
        (click)="onClickSettings()"
        ><img
          src="assets/img/settings-button-icon.svg"
          alt="settings icon"
          class="icon"
      /></app-button>
    </div>
    <app-login-information></app-login-information>
    <div class="to-admin">
      <app-button
        [type]="'button'"
        [disabled]="false"
        (click)="hRouter.navigateByUrl('/admin')"
        >To admin page</app-button
      >
    </div>
  </div>
</header>
