import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, map, switchMap } from 'rxjs';
import { IItem } from '../models/search-item.model';
import { IResponse } from '../models/search-response.model';

@Injectable({
  providedIn: 'root',
})
export class YoutubeService {
  private HTTP_URL = 'https://www.googleapis.com/youtube/v3/';

  private HTTP_KEY = 'AIzaSyCaIsx1V1ywnN-Selao8dbuKMf2_a-BOuY';

  constructor(private http: HttpClient) {}

  searchItems(value: string, limit = 8): Observable<IResponse> {
    return this.http
      .get<IResponse>(
      `${this.HTTP_URL}search?key=${this.HTTP_KEY}&type=video&part=snippet&maxResults=${limit}&q=${value}`,
    )
      .pipe(
        map((resp: IResponse) => {
          const ids: string = resp.items
            .map((item: IItem) => item.id.videoId)
            .join(',');
          return ids;
        }),
        switchMap((ids: string) => this.getItem(ids)),
      );
  }

  getItem(ids: string): Observable<IResponse> {
    return this.http.get<IResponse>(
      `${this.HTTP_URL}videos?key=${this.HTTP_KEY}&id=${ids}&part=snippet,statistics`,
    );
  }
}
